PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE Guests (
    guest_id INTEGER PRIMARY KEY AUTOINCREMENT,
    first_name TEXT NOT NULL,
    last_name TEXT NOT NULL,
    email TEXT UNIQUE NOT NULL,
    phone TEXT,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    preferences TEXT
);
CREATE TABLE RoomTypes (
    room_type_id INTEGER PRIMARY KEY AUTOINCREMENT,
    name TEXT NOT NULL,
    description TEXT,
    base_price DECIMAL(10,2) NOT NULL,
    capacity INTEGER NOT NULL
);
CREATE TABLE Rooms (
    room_number TEXT PRIMARY KEY,
    room_type_id INTEGER NOT NULL,
    status TEXT CHECK(status IN ('disponible','ocupada','mantenimiento','limpieza')) NOT NULL,
    floor INTEGER,
    last_maintenance_date DATE,
    FOREIGN KEY (room_type_id) REFERENCES RoomTypes(room_type_id)
);
CREATE TABLE Reservations (
    reservation_id TEXT PRIMARY KEY,
    guest_id INTEGER NOT NULL,
    check_in_date DATE NOT NULL,
    check_out_date DATE NOT NULL,
    booking_date DATE DEFAULT (DATE('now')),
    total_price DECIMAL(10,2) NOT NULL,
    status TEXT CHECK(status IN ('pendiente','confirmada','cancelada','check-in','check-out')) NOT NULL,
    special_requests TEXT,
    FOREIGN KEY (guest_id) REFERENCES Guests(guest_id)
);
CREATE TABLE ReservationRooms (
    reservation_id TEXT NOT NULL,
    room_number TEXT NOT NULL,
    price_at_booking DECIMAL(10,2) NOT NULL,
    PRIMARY KEY (reservation_id, room_number),
    FOREIGN KEY (reservation_id) REFERENCES Reservations(reservation_id),
    FOREIGN KEY (room_number) REFERENCES Rooms(room_number)
);
CREATE TABLE Payments (
    payment_id INTEGER PRIMARY KEY AUTOINCREMENT,
    reservation_id TEXT UNIQUE NOT NULL,
    payment_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    amount DECIMAL(10,2) NOT NULL,
    payment_method TEXT CHECK(payment_method IN ('tarjeta','efectivo','transferencia')) NOT NULL,
    transaction_id TEXT,
    status TEXT CHECK(status IN ('completado','pendiente','reembolsado')) NOT NULL,
    FOREIGN KEY (reservation_id) REFERENCES Reservations(reservation_id)
);
CREATE TABLE Reviews (
    review_id INTEGER PRIMARY KEY AUTOINCREMENT,
    guest_id INTEGER NOT NULL,
    room_number TEXT NOT NULL,
    reservation_id TEXT,
    rating INTEGER NOT NULL CHECK(rating BETWEEN 1 AND 5),
    comment TEXT,
    review_date DATE DEFAULT (DATE('now')),
    FOREIGN KEY (guest_id) REFERENCES Guests(guest_id),
    FOREIGN KEY (room_number) REFERENCES Rooms(room_number),
    FOREIGN KEY (reservation_id) REFERENCES Reservations(reservation_id)
);
CREATE TABLE Amenities (
    amenity_id INTEGER PRIMARY KEY AUTOINCREMENT,
    name TEXT NOT NULL,
    description TEXT
);
CREATE TABLE RoomAmenities (
    room_number TEXT NOT NULL,
    amenity_id INTEGER NOT NULL,
    PRIMARY KEY (room_number, amenity_id),
    FOREIGN KEY (room_number) REFERENCES Rooms(room_number),
    FOREIGN KEY (amenity_id) REFERENCES Amenities(amenity_id)
);
DELETE FROM sqlite_sequence;
COMMIT;
